import { renderChart } from "./chart.js";
import { getReadings, groupByDay, sortByTime } from "./reading";

const readings = await getReadings();
renderChart(sortByTime(groupByDay(readings)).slice(-30));

//button background color change when click
var arr = document.getElementsByClassName("btnn");
var btn1 = document.getElementById("btn1");
var btn2 = document.getElementById("btn2");

btn1.addEventListener("click", function () {
  renderChart(sortByTime(groupByDay(readings)).slice(-30));
});

btn2.addEventListener("click", function () {
  renderChart(sortByTime(groupByDay(readings)).slice(-10));
});

for (var i = 0; i < arr.length; i++) {
  arr[i].index = i;
  arr[i].onclick = function () {
    for (var j = 0; j < arr.length; j++) {
      arr[j].className = arr[j].className.replace(" bactive", "").trim();
    }
    this.className = this.className + " bactive";
  };
  console.log(1);
}
